mapper.insert:

      SELECT 'GREG' || LPAD(SEQ_GREG.NEXTVAL, 4, '0') FROM DAUL

		INSERT INTO GEN_REG(GEN_REG_ID, SIT_BACKUP_ID, TITLE, START_DATE, END_DATE
		, START_TIME, END_TIME, REG_DATE, INTRODUCTION)
		VALUES(#{gen_reg_id}, #{sit_backup_id}, #{title}
		, #{start_date}, #{end_date}
		, #{start_time}, #{end_time}, SYSDATE, #{introduction})

mapper.select:
SELECT COUNT(*) AS COUNT
FROM GEN_REG_POSSIBLE_VIEW R JOIN GEN_SIT_INFO_VIEW SV
ON R.SIT_BACKUP_ID = SV.SIT_BACKUP_ID
    JOIN SIT_GRADE_VIEW G
    ON R.SIT_BACKUP_ID = G.SIT_BACKUP_ID
        JOIN SIT_REG SR
        ON SV.SIT_BACKUP_ID = SR.SIT_BACKUP_ID
WHERE R.START_DATE <= #{start_date}
  AND R.END_DATE >= #{end_date}
  AND R.START_TIME <= #{start_time}
  AND R.END_TIME >= #{end_time}

SELECT * FROM ( SELECT ROWNUM AS RNUM, DATA.* FROM ( SELECT R.GEN_REG_ID, R.SIT_BACKUP_ID, R.NAME, SV.GENDER, SV.AGE , SR.FILE_PATH AS PHOTO_FILE_PATH, G.GRADE , (SELECT FILE_PATH FROM GRADES WHERE NAME = G.GRADE) AS GRADE_FILE_PATH , NVL(RV.AVG_RATING,0) AS AVG_RATING , NVL(RV.REVIEW_COUNT,0) AS REVIEW_COUNT , NVL(RRV.AVG_RATING,0) AS RECENT_AVG_RATING , NVL(RRV.REVIEW_COUNT,0) AS RECENT_REVIEW_COUNT , R.TITLE, R.INTRODUCTION, R.START_DATE, R.END_DATE, R.START_TIME, R.END_TIME , CASE WHEN EXISTS ( SELECT 1 FROM GEN_REQ Q WHERE R.GEN_REG_ID = Q.GEN_REG_ID ) THEN '예약중' ELSE '예약가능' END AS STATUS FROM GEN_REG_POSSIBLE_VIEW R JOIN GEN_SIT_INFO_VIEW SV ON R.SIT_BACKUP_ID = SV.SIT_BACKUP_ID JOIN SIT_GRADE_VIEW G ON R.SIT_BACKUP_ID = G.SIT_BACKUP_ID JOIN SIT_REG SR ON SV.SIT_BACKUP_ID = SR.SIT_BACKUP_ID LEFT JOIN RAITING_VIEW RV ON R.SIT_BACKUP_ID = RV.SIT_BACKUP_ID LEFT JOIN RECENT_RATING_VIEW RRV ON R.SIT_BACKUP_ID = RRV.SIT_BACKUP_ID WHERE R.START_DATE <= #{start_date} AND R.END_DATE >= #{end_date} AND R.START_TIME <= #{start_time} AND R.END_TIME >= #{end_time}
AND GRADE IN
#{grade}
AND (
SV.GENDER = '여' SV.GENDER = '남' 1=0
)
AND AGE IN
#{age}
ORDER BY STATUS, R.REG_DATE ) DATA WHERE ROWNUM <= #{end,jdbcType=INTEGER} ) WHERE RNUM >= #{start,jdbcType=INTEGER}
