mapper.update:

 	UPDATE CERT_REG
	SET CERT_TYPE_ID = #{cert_reg_id}, FILE_PATH = #{file_path} 
	WHERE SIT_REG_ID = #{sit_reg_id}

mapper.select:

 	SELECT COUNT(*) AS COUNT
	FROM CERT_REG
	WHERE SIT_REG_ID = #{sit_reg_id}


 	SELECT CERT_TYPE_ID, FILE_PATH
	FROM CERT_REG
	WHERE SIT_REG_ID = #{sit_reg_id}
