mapper.update:

 		UPDATE SIT_REG 
		 RIGHT JOIN SIT_BACKUP ON (SIT_REG.SIT_BACKUP_ID = SIT_BACKUP.SIT_BACKUP_ID)
		   SET SIT_REG.PW = #{pw}
		     , SIT_REG.NAME = #{name}
		     , SIT_REG.ROAD_ADDR = #{road_addr}
		     , SIT_REG.DETAILED_ADDR = #{detailed_addr}
		     , SIT_REG.ZIP_CODE = #{zip_code}
		     , SIT_REG.TEL =  #{tel}
		WHERE SIT_BACKUP_ID = #{sit_backup_id}

mapper.select:
SELECT SREG.NAME, SREG.TEL, T.SIT_BACKUP_ID, T.TOTALWORKTIME, CASE WHEN T.TOTALWORKTIME < 120 THEN '신입' WHEN T.TOTALWORKTIME < 1000 THEN '브론즈' WHEN T.TOTALWORKTIME < 5000 THEN '실버' WHEN T.TOTALWORKTIME < 10000 THEN '골드' ELSE '플래티넘' END AS GRADE , SREG.SIT_REG_ID , SCHK.CHK_DATE FROM ( SELECT SIT_BACKUP_ID, SUM(근무시간) AS TOTALWORKTIME FROM ( SELECT G.SIT_BACKUP_ID, SUM((Q.END_DATE - Q.START_DATE +1) * LEAST((Q.END_TIME - Q.START_TIME), 8)) AS 근무시간 FROM GEN_CONFIRMED C JOIN GEN_REQ Q ON C.GEN_REQ_ID = Q.GEN_REQ_ID JOIN GEN_REG R ON Q.GEN_REG_ID = R.GEN_REG_ID JOIN GEN_REG G ON R.GEN_REG_ID = G.GEN_REG_ID WHERE R.START_DATE IS NOT NULL AND R.END_DATE IS NOT NULL AND NOT EXISTS ( SELECT 1 FROM GEN_PAR_CONFIRMED_CANCELED PC WHERE PC.GEN_CONFIRMED_ID = C.GEN_CONFIRMED_ID ) AND NOT EXISTS ( SELECT 1 FROM GEN_SIT_CONFIRMED_CANCELED SC WHERE SC.GEN_CONFIRMED_ID = C.GEN_CONFIRMED_ID ) GROUP BY G.SIT_BACKUP_ID
UNION ALL
SELECT E.SIT_BACKUP_ID, SUM((R.END_DATE - R.START_DATE +1) * LEAST((R.END_TIME - R.START_TIME), 8)) AS 근무시간 FROM EMG_CONFIRMED E JOIN EMG_REQ R ON E.EMG_REQ_ID = R.EMG_REQ_ID WHERE R.START_DATE IS NOT NULL AND R.END_DATE IS NOT NULL GROUP BY E.SIT_BACKUP_ID ) GROUP BY SIT_BACKUP_ID ) T JOIN SIT_REG SREG ON SREG.SIT_BACKUP_ID = T.SIT_BACKUP_ID JOIN SIT_CHK SCHK ON SREG.SIT_BACKUP_ID = SCHK.SIT_BACKUP_ID WHERE T.SIT_BACKUP_ID = #{sit_backup_id}
