mapper.select:
SELECT R.GEN_REG_ID, R.NAME, R.TITLE, R.START_DATE, R.END_DATE, R.START_TIME, R.END_TIME
     , CASE WHEN NOT EXISTS 
                (
                    SELECT 1
                    FROM GEN_REQ Q
                    WHERE R.GEN_REG_ID = Q.GEN_REG_ID
                ) THEN '예약중'
            ELSE '예약가능'
            END AS STATUS
FROM GEN_REG_POSSIBLE_VIEW R
WHERE R.START_DATE <= #{start_date}
  AND R.END_DATE >= #{end_date}
  AND R.START_TIME <= #{start_time}
  AND R.END_TIME >= #{end_time}

SELECT COUNT(*) AS COUNT
FROM GEN_REG_POSSIBLE_VIEW R
WHERE R.START_DATE <= #{start_date}
  AND R.END_DATE >= #{end_date}
  AND R.START_TIME <= #{start_time}
  AND R.END_TIME >= #{end_time}
